INSERT INTO `Help_type` (`id`,`title`,`desc2`,`description`,`desc1`) VALUES ('3','141003','限时活动帮助信息','140003','1、限时活动以七天为一个周期，每天为一个阶段。每阶段结束都会发放积分阶段奖励和积分排名奖励。整个活动结束时再根据每阶段的积分总和情况，发放总积分排名奖励。
