INSERT INTO `Email_tips` (`id`,`time`,`title`,`title_desc1`,`desc`,`desc1`) VALUES ('7','2520','145006','【温馨提示】招募武将','144006','集齐武将信物可以在酒馆中招募该武将。武将信物可在世界地图上通过攻打匪徒NPC或者集结军团后攻打营寨的方式来获得。部分商店有武将信物可以购买。') ON DUPLICATE KEY UPDATE `id` = '7',`time` = '2520',`title` = '145006',`title_desc1` = '【温馨提示】招募武将',`desc` = '144006',`desc1` = '集齐武将信物可以在酒馆中招募该武将。武将信物可在世界地图上通过攻打匪徒NPC或者集结军团后攻打营寨的方式来获得。部分商店有武将信物可以购买。';
