INSERT INTO `Email_tips` (`id`,`time`,`title`,`title_desc1`,`desc`,`desc1`) VALUES ('10','3960','145008','【温馨提示】资源获得','144008','城内的农田、金矿、伐木场等资源设施会定期生产出相应的资源。另外您还可以在世界地图上采集资源田获取额外资源。军团的负重越高可采集的资源越多。※点击主公头像内的【帮助】菜单可以了解如何提高资源的采集效果。') ON DUPLICATE KEY UPDATE `id` = '10',`time` = '3960',`title` = '145008',`title_desc1` = '【温馨提示】资源获得',`desc` = '144008',`desc1` = '城内的农田、金矿、伐木场等资源设施会定期生产出相应的资源。另外您还可以在世界地图上采集资源田获取额外资源。军团的负重越高可采集的资源越多。※点击主公头像内的【帮助】菜单可以了解如何提高资源的采集效果。';
