INSERT INTO `Email_tips` (`id`,`time`,`title`,`title_desc1`,`desc`,`desc1`) VALUES ('12','4920','145010','【温馨提示】部队负重','144010','提高部队总负重量，可提高部队每次采集和抢夺的资源数量，使得部队行军事半功倍。提高负重的方法：1、驻守或升级校场提高部队士兵数量上限。2、尽可能使用高负重的车兵（冲车、攻城锤、攻城车等）') ON DUPLICATE KEY UPDATE `id` = '12',`time` = '4920',`title` = '145010',`title_desc1` = '【温馨提示】部队负重',`desc` = '144010',`desc1` = '提高部队总负重量，可提高部队每次采集和抢夺的资源数量，使得部队行军事半功倍。提高负重的方法：1、驻守或升级校场提高部队士兵数量上限。2、尽可能使用高负重的车兵（冲车、攻城锤、攻城车等）';
