INSERT INTO `Email_tips` (`id`,`time`,`title`,`title_desc1`,`desc`,`desc1`) VALUES ('15','6360','145013','【温馨提示】联盟福利','144013','加入联盟后，可以享受很多新功能。盟友之间互相帮助还可以大幅减少建筑建造、研究科技、治疗伤兵等所需要的时间。升级屯所并驻守武将，可大大提高帮助的效率。选择联盟时可以参考该联盟的人气度、战斗力等要素。') ON DUPLICATE KEY UPDATE `id` = '15',`time` = '6360',`title` = '145013',`title_desc1` = '【温馨提示】联盟福利',`desc` = '144013',`desc1` = '加入联盟后，可以享受很多新功能。盟友之间互相帮助还可以大幅减少建筑建造、研究科技、治疗伤兵等所需要的时间。升级屯所并驻守武将，可大大提高帮助的效率。选择联盟时可以参考该联盟的人气度、战斗力等要素。';
