INSERT INTO `Email_tips` (`id`,`time`,`title`,`title_desc1`,`desc`,`desc1`) VALUES ('17','7320','145015','【温馨提示】士兵数量','144015','粮食总产量会直接影响士兵持有的总数量。粮食总产量可以通过农田升级、农田驻守、升级相关主公天赋、研究农田相关科技来增加。资源【产量】影响城内自动生产的资源的产量。而资源【采集速度】则影响城外开采资源时的效率。') ON DUPLICATE KEY UPDATE `id` = '17',`time` = '7320',`title` = '145015',`title_desc1` = '【温馨提示】士兵数量',`desc` = '144015',`desc1` = '粮食总产量会直接影响士兵持有的总数量。粮食总产量可以通过农田升级、农田驻守、升级相关主公天赋、研究农田相关科技来增加。资源【产量】影响城内自动生产的资源的产量。而资源【采集速度】则影响城外开采资源时的效率。';
INSERT INTO `Email_tips` (`id`,`time`,`title`,`title_desc1`,`desc`,`desc1`) VALUES ('18','7800','145016','【温馨提示】武将装备','144016','武将的防具和饰品可以通过攻击世界地图上的匪徒NPC和营寨、领取游戏的在线奖励等方式获得。可在背包、铁匠铺、酒馆中点击装备查看其属性和特技。在装备武将时请注意武将和装备的特技的匹配性。※点击左上角主公头像可以找到【帮助】菜单查看相关信息。') ON DUPLICATE KEY UPDATE `id` = '18',`time` = '7800',`title` = '145016',`title_desc1` = '【温馨提示】武将装备',`desc` = '144016',`desc1` = '武将的防具和饰品可以通过攻击世界地图上的匪徒NPC和营寨、领取游戏的在线奖励等方式获得。可在背包、铁匠铺、酒馆中点击装备查看其属性和特技。在装备武将时请注意武将和装备的特技的匹配性。※点击左上角主公头像可以找到【帮助】菜单查看相关信息。';
